pragma solidity ^0.5.1;

contract ApartmentContract {
    string public name;
    uint256 public totalSum;
    uint256 public paidSum = 0;
    uint256 oneEther = 1000000000000000000;
    address payable seller;
    address payable buyer;
    enum Status {Cancelled, Finished, Active}
    Status public status;

    function createContract(
        string memory _name,
        uint256 _totalSum,
        address payable _seller
    ) public {
        name = _name;
        totalSum = _totalSum * oneEther;
        status = Status.Active;
        seller = _seller;
    }

    function addPayment() public payable {
        require(status == Status.Active);
        paidSum += msg.value;
        seller.transfer(msg.value);
        if (paidSum >= totalSum) setFinished();
    }

    function getPaidSum() public view returns (uint256) {
        return paidSum;
    }

    function getDifference() public view returns (uint256) {
        return totalSum - paidSum;
    }

    function isActive() public view returns (bool) {
        return status == Status.Active;
    }

    function getStatus() public view returns (Status) {
        return status;
    }

    function setCancelled() public {
        require(status == Status.Active);
        status = Status.Cancelled;
        //return 90% of the sum to the buyer
    }

    function setFinished() private {
        status = Status.Finished;
    }
}
